const canUse = $.require("hiker://page/wait");
if (canUse) {
    var items = [];
    var self = JSON.parse(getRule()).title;
    var setjson = JSON.parse(fetch('hiker://files/rules/xyq/appmset.json', {}));
    var ssmd = setjson.ssmode;
    var ssxc = setjson.sscount;
    var locver = MY_RULE.version;
    //打乱顺序
    var random = true;

    function randomSort3(arr) {
        arr.sort(function(a, b) {
            return Math.random() - 0.5;
        });
        return arr;
    }

    items.push({
        title: '🔍设置' + '(' + (ssmd == 1 ? '聚' + ssxc : '列') + ')',
        //title : '🔍设置'+'('+ssxc+')',
        url: $('hiker://empty').rule((locver) => {
            eval(JSON.parse(fetch('hiker://page/setting', {})).rule);
        }, locver),
        col_type: 'flex_button'
    });

    items.push({
        title: '资源网采集',
        url: 'hiker://home@资源网采集.xyq||https://haikuoshijie.cn/topic/6033',
        col_type: 'flex_button'
    });
    items.push({
        title: '香情影视',
        url: 'hiker://home@香情影视||https://mp.weixin.qq.com/s/XpUI3F1nBvlNgHXvY71t0g',
        col_type: 'flex_button'
    });
    items.push({
        title: "搜索",
        url: $.toString(() => {
            var link = 'hiker://empty#noRecordHistory#$$$' + input + '$$$fypage$$$';
            //log(link);
            return $(link).rule(() => {
                $.require('hiker://page/sear');
            });
        }),
        extra: {
            onChange: "putVar('xyqappystext',input)",
            titleVisible: true
        },
        desc: "请输入搜索关键词",
        col_type: "input"
    });

    var ssyq = ['资源网采集搜@@资源网采集.xyq', '香情影视搜@@香情影视'];
    if (self !== 'APP影视(P)') {
        items.push({
            title: self + '搜',
            url: $("#noLoading#").lazyRule(rule => 'hiker://search?s=' + getVar('xyqappystext') + '&rule=' + rule, self),
            col_type: 'flex_button'
        });
    } else {
        for (var yq in ssyq) {
            var kj = ssyq[yq].split('@@');
            items.push({
                title: kj[0],
                url: $("#noLoading#").lazyRule(rule => 'hiker://search?s=' + getVar('xyqappystext') + '&rule=' + rule, kj[1]),
                col_type: "flex_button"
            });
        }
    }
    items.push({
        title: '茶杯狐搜',
        url: $('hiker://empty#x#fypage@-1@*24@#x#').rule(() => {
            var res = {};
            var d = [];
            var spl = MY_URL.split('#x#');
            //var lin = 'https://api2.jackeriss.com/api/v1/search/?text=' + spl[1] + '&type=0&from=' + spl[2] + '&size=20';
            var lin = 'https://api.jackeriss.com/api/v1/search/?text=' + getVar('xyqappystext') + '&type=0&from=' + spl[1] + '&size=24';
            var pn = spl[1] / 24 + 1;
            var urlo = JSON.parse(request(lin, {}));
            var urlt = JSON.parse(fetch(lin.replace('type=0', 'type=1'), {}));
            //log(urlo);
            if (urlo.resources.length < 1 && urlt.resources.length < 1) {
                d.push({
                    title: '当前关键字  ' + getVar('xyqappystext') + '  无搜索结果',
                    col_type: 'text_center_1'
                });
            }
            if (urlo.resources.length > 0) {
                d.push({
                    title: '♥当前第' + pn + '页',
                    col_type: 'text_center_1'
                });

                for (var i = 0; i < urlo.resources.length; i++) {
                    var title = urlo.resources[i].text.replace(/\<.*?\>/g, '');
                    var url = urlo.resources[i].url;
                    var desc = urlo.resources[i].website;
                    d.push({
                        title: title.replace(getVar('xyqappystext'), '““' + getVar('xyqappystext') + '””') + '  ' + desc + '  在线',
                        url: url,
                        //desc: '在线搜索结果',
                        col_type: 'text_1'
                    });
                }
            }

            if (urlt.resources.length > 0) {
                for (var j = 0; j < urlt.resources.length; j++) {
                    var title = urlt.resources[j].text.replace(/\<.*?\>/g, '');
                    var url = urlt.resources[j].url;
                    var desc = urlt.resources[j].website;
                    d.push({
                        title: title.replace(getVar('xyqappystext'), '““' + getVar('xyqappystext') + '””') + '  ' + desc + '  下载',
                        url: url,
                        //desc: '下载搜索结果',
                        col_type: 'text_1'
                    });
                }
            }
            res.data = d;
            setResult(res);
        }),
        col_type: "flex_button"
    });
    try {
        var appfile = fetchCache('https://inmemory.coding.net/p/InMemory/d/MBrowser/git/raw/master/AppFile/APP影视列表', 24);
    } catch (e) {
        appfile = '';
    }
    if (/InMemory/.test(appfile)) {
        //log('当前加载的是缓存数据');
        var html = JSON.parse(appfile);
    } else {
        log('当前加载的是内置数据');
        var html = JSON.parse(JSON.parse(fetch('hiker://page/ResCode', {})).rule);
    };
    var conts = html.data;
    for (var i = 0; i < conts.length; i++) {
        var list = conts[i].list;
        var tytit = conts[i].title;
        items.push({
            title: tytit + ' ' + [list.length],
            url: 'hiker://empty',
            col_type: 'text_1'
        });

        if (random) {
            let k = getVar(MY_RULE.title + "-" + tytit, "");
            if (k == "") {
                list = randomSort3(list)
                putVar(MY_RULE.title + "-" + tytit, JSON.stringify(list))
            } else {
                list = JSON.parse(k)
            }
        }
        for (var j = 0; j < list.length; j++) {
            var title = list[j].title;
            var turl = list[j].url;
            var img = list[j].img;
            if (/TV/.test(tytit)) {
                var url = 'hiker://empty$$' + tytit + '$$' + turl + '$$?ac=list&class=&page=fypage&area=&type=&start=$$';
            } else {
                if (turl.indexOf('api.php/app/') != -1) {
                    var url = 'hiker://empty$$' + tytit + '$$' + turl + '$$index_video?token=$$';
                } else if (turl.indexOf('xgapp.php/v1/') != -1) {
                    var url = 'hiker://empty$$' + tytit + '$$' + turl + '$$index_video?token=$$';
                } else {
                    var url = 'hiker://empty$$' + tytit + '$$' + turl + '$$/vodPhbAll$$';
                }
            }
            items.push({
                title: title,
                url: url,
                pic_url: img,
                col_type: 'icon_round_small_4'
            });
        }
    }
    setResult({
        data: items
    });
}

