var { dataLoad, runCode, urlParse } = $.require('hiker://page/utility?rule=' + getItem('myCollection'))
let myCollection_el = [],
    searchThd = parseInt(readFile('searchThd') || '5'),
    keyword = getParam('keyword'),
    pageNum = parseInt(getParam('page')),
    RULES = dataLoad().filter((v) => v.search_url)
if (searchThd == 0) {
    myCollection_el.push({
        title: ('⬇ 选择一个小程序查看' + keyword + '的搜索结果 ⬇').small(),
        url: 'hiker://empty'
    })
    RULES.forEach((v) => myCollection_el.push({
        title: v.title,
        url: "hiker://page/singleSearch?keyword=" + keyword + "&page=fypage",
        extra: { rule: getItem('myCollection'), RULE: v },
    }))
} else if (searchThd > 0) {
    let searchCount = pageNum * searchThd,
        searchTag = { title: '⬆ 已搜索了' + searchCount + '个小程序 ⬆\n⬇ 下面还有' + RULES.slice(searchCount).length + '个小程序 ⬇' }
    RULES = RULES.slice(searchCount - searchThd, searchCount)
    if (!RULES.length) setResult([])

    let URLS = RULES.map((RULE) => {
        MY_RULE.ua = RULE.ua
        let urlReq = urlParse(RULE.search_url, {}, 1, (url) => {
            url[0] = url[0].replace(url[0].includes('%%') ? /%%/g : /\*\*/g, encodeStr(keyword, url[2]))
        })
        return { url: urlReq.MY_URL, options: urlReq.MY_URL_Options }
    })
    batchFetch(URLS).forEach((indexHtml, index) => {
        // 处理MY_URL和MY_RULE
        let RULE = RULES[index]
        MY_URL = URLS[index].url
        MY_RULE.ua = RULE.ua
        MY_RULE.col_type = RULE.col_type
        MY_RULE.detail_col_type = ['', '*'].includes(RULE.sdetail_find_rule) ? RULE.detail_col_type : RULE.sdetail_col_type
        MY_RULE.find_rule = RULE.searchFind
        MY_RULE.detail_find_rule = ['', '*'].includes(RULE.sdetail_find_rule) ? RULE.detail_find_rule : RULE.sdetail_find_rule
        MY_RULE.preRule = RULE.preRule
        MY_RULE.pageList = JSON.parse(RULE.pages || '[]')
        MY_RULE.pages = JSON.stringify(MY_RULE.pageList)
        MY_RULE.last_chapter_rule = RULE.last_chapter_rule
        MY_RULE.params = {}
        MY_RULE.url = MY_URL
        MY_RULE.urlHeaders = URLS[index].options.headers

        // 正文解析
        eval(MY_RULE.preRule)
        if (MY_RULE.find_rule.startsWith('js:')) {
            function getUrl() {
                return MY_URL
            }

            function getResCode() {
                return indexHtml
            }

            setResult = function(el) {
                if (Array.isArray(el.data)) el = el.data
                Array.prototype.push.apply(myCollection_el, el.map((v) => {
                    let MY__RULE = Object.assign({}, MY_RULE, { title: RULE.title })
                    v.desc = RULE.title + ' ▪ ' + (v.desc || '')

                    if (!v.url || ['rule', 'pics', 'toast', 'input', 'copy', 'editFile', 'x5', 'x5WebView', 'x5Play', 'web', 'select', 'x5Rule', 'webRule'].find((vv) => v.url.startsWith(vv + '://'))) {
                        return v
                    } else if (v.url.includes('@rule=')) {
                        let [_, url, rule] = v.url.match(/^([\s\S]*?)@rule=([\s\S]*)$/)
                        v.url = url
                        MY__RULE.detail_find_rule = rule
                        MY__RULE.detail_col_type = MY_RULE.col_type
                    } else if (v.url.startsWith('hiker://page/')) {
                        if (v.url.includes('rule=') || (v.extra || {}).rule)
                            return v
                        let [_, path, params] = v.url.split('#')[0].match(/^hiker:\/\/page\/(.+?)(?:\?(.*))?$/),
                            subPage = MY_RULE.pageList.find((v) => v.path == path),
                            subUrl = (params || '').split('&').find((v) => v.startsWith('url='))
                        v.url = subUrl ? subUrl.slice(4).replace(/？？/g, '?').replace(/＆＆/g, '&') : (v.extra || {}).url || 'hiker://empty?' + (params || '')
                        MY__RULE.detail_find_rule = subPage.rule
                        MY__RULE.detail_col_type = subPage.col_type
                        MY__RULE.params = v.extra || {}
                    } else if (v.url.includes('@lazyRule=')) {
                        v.url = v.url.replace('.js:', '.js:\nif(MY_RULE)Object.assign(MY_RULE,' + $.stringify({ pages: MY_RULE.pages, pageList: MY_RULE.pageList, find_rule: '', params: '' }) + ');MY_URL="' + MY_URL + '";')
                        return v
                    } else if (!MY_RULE.detail_find_rule || v.url.startsWith('hiker://')) { return v }
                    v.extra = { url: v.url, RULE: MY__RULE, pageTitle: v.title }
                    v.url = 'hiker://page/detailLoad?rule=' + getItem('myCollection')
                    if (v.extra.RULE.url.includes('#immersiveTheme#')) v.url += '&#immersiveTheme#'

                    return v
                }))
            }
            setHomeResult = setResult
            setSearchResult = setResult

            try {
                eval(MY_RULE.find_rule.slice(3))
            } catch (e) {}
        } else {
            let findRule = MY_RULE.find_rule.split(';')
            parseDomForArray(indexHtml, findRule.shift()).forEach((data) => {
                let [title, url, desc, content, img] = findRule.map((v, i) => {
                        try {
                            if (v == '*') return ''
                            else v = (i == 1 || i == 4) ?
                                parseDom(data, v) :
                                parseDomForHtml(data, v)
                            if (i != 1) v = runCode(v)
                            return v
                        } catch (e) { return '' }
                    }),
                    res = {
                        title: title,
                        url: url,
                        desc: RULE.title + ' ▪ ' + desc,
                        content: content,
                        img: img
                    }
                if (res.url) {
                    let MY__RULE = Object.assign({}, MY_RULE, { title: RULE.title })
                    if (res.url.includes('@lazyRule=')) {
                        res.url = res.url.replace('.js:', '.js:\nif(MY_RULE)Object.assign(MY_RULE,' + $.stringify({ pages: MY__RULE.pages, pageList: MY__RULE.pageList, find_rule: '', params: '' }) + ');MY_URL="' + MY_URL + '";')
                    } else if (MY_RULE.detail_find_rule) {
                        res.extra = { url: url, RULE: MY__RULE, pageTitle: title }
                        res.url = 'hiker://page/detailLoad?rule=' + getItem('myCollection')
                        if (res.extra.RULE.url.includes('#immersiveTheme#')) res.url += '&#immersiveTheme#'
                    }
                }
                myCollection_el.push(res)
            })
        }
    })
    myCollection_el.push(searchTag)
}
method_setResult.invoke(javaContext, myCollection_el, CALLBACK_KEY, MY_RULE, MY_TYPE)

