function LazyParseFast(playUrlList,getTimeout,x5Timeout){
    const {isVideo,getHeaders,x5ParsePro} = $.require('hiker://page/globalParse?rule=道长仓库Pro');
    // log('开始执行'+playUrlList.length+'个地址的批量嗅探:'+playUrlList);
