    split(text, target, count) {
        count = count === undefined ? 1 : count;
        let array = [text];
        for (let i = 0; i < count; i++) {
            text = array[i];
            let index = text.indexOf(target);
            if (index === -1) {
                break;
            }
            array[i] = text.slice(0, index);
            array[i + 1] = text.slice(index + target.length);
        }
        return array;
    },
    chunk(arr, size) {
        var result = [];
        for (var i = 0, len = arr.length; i < len; i += size) {
            result.push(arr.slice(i, i + size));
        }
        return result;
    },
    hikerPic(url) {
        var headers = {};
        let urls = url.split("@");
        let picUrl = urls.shift();
        let length = urls.length;
        for (let i = 0; i < length; i++) {
            let [key, value] = urls[i].split("=");
            headers[key] = value;
        }
        return [picUrl, headers];
    },
    range(start, end) {
        return Array(end - start).fill(0).map((el, i) => start + i);
    }
}"},{"col_type":"movie_3","name":"#j.Config","path":"Config.json","rule":"const Config = Object.assign({
