setResult([{
    title: "点我开始轻合集•视界聚搜",
    url: "hiker://search?s=" + getParam("keyword"),
    extra: {
        rules: $.toString(() => {
            const defaultDisTabModify = false; //默认禁用自定义分类关闭.就是开启自定义
            const QING_TITLE = getMyVar('myCollection'),
                typeShow = getMyVar('typeShow', '');
                let disTabModify = readFile('disTabModify');
                if(disTabModify===''){
                    saveFile('disTabModify', defaultDisTabModify+'');
                    disTabModify = defaultDisTabModify;
                }else if(/true|false/.test(disTabModify)){
                    disTabModify = JSON.parse(disTabModify);
                }
            let rules = $.require('hiker://page/dataLoad?rule=' + QING_TITLE)();
            if (disTabModify&&typeShow) {
                rules = rules.filter((v) => v.type === typeShow);
            }else if(!disTabModify){
                typeNow = typeShow || '全部';
                if(typeNow==='其他'){
                    rules = rules.filter((v) => !v.sort_url);
                }else if(typeNow!=='全部'){
                    rules = rules.filter((v) => v.sort_url===typeNow);
                }
            }
            return JSON.stringify(rules)
        })
    }
}]);

