"},{"col_type":"movie_3","name":"数据载入","path":"dataLoad","rule":"$.exports = function(showAll) {
    let url = getMyVar('remoteUrl'),
        data = []
    if (url.startsWith('hiker://') || url.startsWith('file://')) {
        // 如果为本地文件则不进行缓存
        try {
            let oriData = fetch(url)
            if (url.startsWith('hiker://page/'))
                oriData = JSON.parse(oriData).rule
            data = JSON.parse(oriData)
        } catch (e) {}
    } else {
        // 获取远程数据，检查间隔默认一天
        let interval = parseInt(readFile('updateInterval') || '1'),
            oldData = fetch('hiker://files/libs/' + md5(url) + '.js'),
            remoteData = fetchCache(url, 24 * interval)
        try { remoteData = JSON.parse(remoteData) } catch(e) {}
        if(!Array.isArray(remoteData)) remoteData = []
        if (remoteData.length < 3) {
            log('未获取到数据')
            if (oldData) writeFile('hiker://files/libs/' + md5(url) + '.js', oldData)
            data = JSON.parse(oldData || '[]')
        } else {
            log('已获取到数据')
            data = remoteData
        }
    }
    data = data.filter((v) => { return v.title != getMyVar('myCollection') && !(v.author || '').includes('轻合集生成器') })
    if (readFile('disableCustom')) return data
    else { // 写入自定义数据
        let customData = JSON.parse(readFile('customData') || '[]'),
            rewriteData = []
        customData = customData.reduce((self, v) => {
            let index = data.findIndex((vv) => v.title == vv.title)
            if (index >= 0) {
                self.push(v)
                let rule = data.splice(index, 1)[0]
                if (showAll || v.visible)
                    rewriteData.push(Object.assign(rule, v))
            }
            return self
        }, [])
        data.forEach((v) => customData.push({ title: v.title, visible: true }))
        saveFile('customData', JSON.stringify(customData))
        return rewriteData.concat(data)
    }
}
