function getNowFormatDate() {
            let date = new Date();
            let seperator1 = "-";
            let seperator2 = ":";
            let month = date.getMonth() + 1;
            let strDate = date.getDate();
            let strHours = date.getHours();
            let strMinutes = date.getMinutes();
            let strSeconds = date.getSeconds()+1;
            if (month >= 1 && month <= 9) {
                month = "0" + month
            }
            if (strDate >= 0 && strDate <= 9) {
                strDate = "0" + strDate
            }
            if (strHours >= 0 && strHours <= 9) {
                strHours = "0" + strHours
            }
            if (strMinutes >= 0 && strMinutes <= 9) {
                strMinutes = "0" + strMinutes
            }
            if (strSeconds >= 0 && strSeconds <= 9) {
                strSeconds = "0" + strSeconds
            }
            //let currentdate = date.getFullYear() + seperator1 + month + seperator1 + strDate + " " + strHours + seperator2 + strMinutes + seperator2 + strSeconds;
            let currentdate = (date.getFullYear()-2020)+''+ month +''+ strDate +''+ strHours+'' + strMinutes +''+ Math.ceil(strSeconds/7);
            return parseInt(currentdate)
        }
$.exports.getNowFormatDate = getNowFormatDate;
