            }
            var txts = net.replace(/#EXTINF:-1 ,/g, '')
                .replace(/#EXTINF:-1,/g, '')
                .replace(/\n#genre#/g, ',#genre#')
                .replace(/#EXTINF:-1/g, '')
                .replace(/group-title="/g, '')
                .replace(/\nhttp/g, ',http')
                //.replace(/=/g, '')
                .replace(/，#/g, ',#')
                .replace(/",/g, '/')
                .replace(/ /g, '')
                .split('\n');
            const d1 = []
            for (var i = 0; i < txts.length; i++) {
                var r = {};
                var j = txts[i].split(',');
                if (txts[i].indexOf("#EXTINF") == 0 &&
                    j.length == 3 && j[2].indexOf("http") == 0) {
                    r.title = j[1].split('/')[j[0].split('/').length - 1];
                    r.url = j[2].replace(`\n`, '').replace('\r', '');
                    if (r.url.indexOf("http") == 0 || r.url.indexOf("rtmp") == 0 || r.url.indexOf("rtsp") == 0) {
                        r.url = r.url + "#isVideo=true#";
                    }
                    d1.push(r);
                    continue;
                }

                if (j.length < 2) {
                    continue;
                }
                r.title = j[0].split('/')[j[0].split('/').length - 1];
                if (filter != null && !r.title.includes(filter)) {
                    continue;
                }
                r.url = j[1].replace(`\n`, '').replace('\r', '');
                if (r.url.indexOf("http") == 0 || r.url.indexOf("rtmp") == 0 || r.url.indexOf("rtsp") == 0) {
                    r.url = r.url + "#isVideo=true#";
                }
                d1.push(r);
            }
            //多线路
            const d2 = {}
            for (let it of d1) {
                if (d2[it.title] == null) {
                    d2[it.title] = []
                }
                d2[it.title].push(it.url)
            }

            for (let it of Object.keys(d2)) {
                _d.push({
                    title: it,
                    url: JSON.stringify({
                        urls: d2[it]
                    })
                })
            }
        } catch (e) {}
        if (_d && _d.length) {
            data = data.concat(_d)
            saveFile("data.json", JSON.stringify(_d));
        } else {
            let _td = readFile("data.json");
            if (_td) {
                data = data.concat(JSON.parse(_td))
            }
        }
    } catch (e) {
        data.push({
            title: "数据加载失败",
            desc: JSON.stringify(e),
            col_type: "text_1"
        })
    }
}
$.exports = {
    renderLiveData: renderLiveData,
}"}]

