var rule_list = JSON.parse(html).result;
let filter = getVar("Warehouse.filter", "");
if (filter !== "") {
    rule_list = rule_list.filter(item => item.author === filter);
}
if (rule_list.length === 0) {
    d.push({
        title: '共搜索到' + rule_list.length + "条记录",
        desc: '详情请找道长,qq 434857005',
        url: "hiker://empty",
        col_type: 'text_center_1'
    });
}
let homeList = JSON.parse(request(api.home));

for (let item of rule_list) {
    //let lver=localRuleVer(homeList, item);
    let label = "",
        lver = -2;
    if (item.data_type === "home_rule_url") {
        lver = localRuleVer(homeList, item);
        if (lver === -1) {
            label = "🆕";
        } else if (lver < item.ver) {
            label = "🆙";
        }
    }
    d.push({
        title: label+item.name,
        desc: item.type,
        content: item.author/*+"\n本地版本:"+lver+"\t\t云端版本:"+item.ver*/+ '\n更新于:' + item.last_update,
        url: "hiker://page/ruleDetail#noHistory##noRecordHistory#",
        extra: {
            lver: lver,
            id: item.id
        },
        pic_url: item.pic_url
    });

}

setResult(d);

