/* 规则列表排序 [ UP、NEW、无更新 ] 顺序 */
/* 归并排序 Start */
function mergeSort(arr) {
    var len = arr.length;
    if (len < 2) {
        return arr;
    }
    var middle = Math.floor(len / 2),
        left = arr.slice(0, middle),
        right = arr.slice(middle);
    return merge(mergeSort(left), mergeSort(right));
}
function merge(left, right) {
    var result = [];
    var isThisVersionList = [];

    while (left.length > 0 && right.length > 0) {
        if (left[0].title.includes("🆙")) {
            result.push(left.shift());
        } else if (right[0].title.includes("🆙")) {
            result.push(right.shift());
        } else if (left[0].title.includes("🆕")) {
            result.push(left.shift());
        } else if (right[0].title.includes("🆕")) {
            result.push(right.shift());
        } else {
            isThisVersionList.push(left.shift());
            isThisVersionList.push(right.shift());
        }
    }

    while (isThisVersionList.length) result.push(isThisVersionList.shift());

    while (left.length)
        result.push(left.shift());

    while (right.length)
        result.push(right.shift());

    return result;
}
$.exports.mergeSort = mergeSort;
/* 归并排序 End */"},{"col_type":"icon_2_round","name":"全部公开规则","path":"AllPublicRule","rule":"js:
