            }, this.sign, folded),
            col_type: 'scroll_button'
        })
        let arr = folded ? [this.arr[0]] : this.arr
        arr.forEach((v) => {
            let { id, class_name, class_url } = v,
            selected = JSON.stringify(this.getClass(id))
            class_name.forEach((name, i) => {
                let url = class_url[i],
                    now = JSON.stringify({ name: name, url: url })
