//设置更新间隔时间,单位为整数天。
var uptime = 2;

function uprulefile() {
    var rulejs = fetch('https://code.aliyun.com/lzk23559/CloudRule/raw/master/hikermovie.js', {});
    if (rulejs.search(/lazyRule/) == -1) {
        rulejs = fetch('https://codeberg.org/lzk23559/cloudrule/raw/branch/master/hikermovie.js', {});
    }
    if (rulejs.search(/lazyRule/) != -1) {
        writeFile("hiker://files/rules/xyq/hikermovie.js", rulejs);
    }
    var rulejson = fetch('https://code.aliyun.com/lzk23559/CloudRule/raw/master/hikermovie.json', {});
    if (rulejson.search(/\"vodhref\"/) == -1) {
        rulejson = fetch('https://codeberg.org/lzk23559/cloudrule/raw/branch/master/hikermovie.json', {});
    }
    if (rulejson.search(/\"vodhref\"/) != -1) {
        writeFile("hiker://files/rules/xyq/hikermovie.json", rulejson);
    }
    if (rulejs.search(/lazyRule/) != -1 && rulejson.search(/\"vodhref\"/) != -1) {
        writeFile("hiker://files/rules/xyq/hikerupdate.txt", new Date() + '');
        confirm({title:'提示', content:'自动更新完成'});
    } else {
        confirm({title:'提示', content:'自动更新失败'});
    }
}



//检测是否有写入时间与规则的js插件文件
if (fileExist("hiker://files/rules/xyq/hikerupdate.txt") && fileExist("hiker://files/rules/xyq/hikermovie.js") && fileExist("hiker://files/rules/xyq/hikermovie.json")) {
    //计算时间间隔
    var start = new Date(fetch("hiker://files/rules/xyq/hikerupdate.txt", {}));
    var nowt = new Date();
    var D_T = nowt.getTime() - start.getTime();
    var D_D = D_T / (1000 * 3600 * 24);
    var tdif = parseInt(D_D) + '';
    //log(tdif);
    //默认间隔超过设置天数就取一次云端文件
    if (tdif >= uptime || tdif < 0) {
        uprulefile();
    }
} else {
    //如果少一个文件就自动取云端文件
    uprulefile();
}



//执行需要预处理的站点
var lac = fetch('hiker://files/rules/xyq/hikermovie.js');
//log(lac.length);
if (lac.search(/lazyRule/) != -1) {
    eval(lac);
    hikerpre();
} else {
    //如果本地文件不对将尝试自修复
    uprulefile();
    setError('规则修复完成，如有问题请反馈。')
}

