        col_type: 'avatar'
    }, {
        col_type:"x5_webview_single",
        desc:"auto",
        url:"file:///storage/emulated/0/Android/data/com.example.hikerview/files/Documents/rules/dzHouse/html/多功能h5插件.html"
    }, {
        url: '"hiker://search?s="+input',
