    }, {
        img: 'https://lanmeiguojiang.com/tubiao/ke/137.png',
        title: '历史',
        url:"hiker://history",
        col_type:"icon_2"
    }, {
        img: 'https://lanmeiguojiang.com/tubiao/messy/10.svg',
        title: '本地资源',
        url:"hiker://page/Main.view?rule=本地资源管理",
        col_type:"avatar"
