[{"col_type":"text_1","name":"ClassTab","path":"ClassTab","rule":"const ClassTab = function(classArray, params) {
    Object.assign(this, params)
    this.arr = classArray.map(v => this.init(v))
    this.sign = '$' + QING_TITLE + '_' + (this.name || '') + '_'
    this.color = this.color || '#12b668'
    this.boundary = this.boundary || 'blank_block'
}
ClassTab.prototype = {
    constructor: ClassTab,
    load(el) {
        let folded = this.fold && getVar('fold_' + this.sign, '')
        if (this.arr.length > 1 && this.fold) el.push({
            title: '““””' + (folded ? '▶' : '▼').fontcolor('#049eff'),
            url: $('#noLoading#').lazyRule((sign, folded) => {
                putVar('fold_' + sign, { '': 'T', 'T': '' } [folded])
                refreshPage(false)
                return 'hiker://empty'
            }, this.sign, folded),
            col_type: 'scroll_button'
        })
        let arr = folded ? [this.arr[0]] : this.arr
        arr.forEach((v) => {
            let { id, class_name, class_url } = v,
            selected = JSON.stringify(this.getClass(id))
            class_name.forEach((name, i) => {
                let url = class_url[i],
                    now = JSON.stringify({ name: name, url: url })
                el.push({
                    title: selected == now ? '““””' + name.fontcolor(this.color).bold() : name,
                    url: selected == now ? 'hiker://empty' : $('#noLoading#').lazyRule((sign, id, now) => {
                        putVar(sign + id, now)
                        refreshPage(false)
                        return 'hiker://empty'
                    }, this.sign, id, now),
                    col_type: 'scroll_button'
                })
            })
            el.push({ col_type: this.boundary })
        })
    },
    init(classObject) {
        if (typeof classObject.class_name == 'string')
            classObject.class_name = classObject.class_name.split('&')
        if (typeof classObject.class_url == 'string')
            classObject.class_url = classObject.class_url.split('&').map((v) => v.trim())
        return classObject
    },
    push(classObject) {
        this.arr.push(this.init(classObject))
    },
    getClass(id) {
        let defaultClass = this.arr.find(item => item.id == id)
        defaultClass = JSON.stringify(defaultClass ? {
            name: defaultClass.class_name[0],
            url: defaultClass.class_url[0]
        } : { name:  'cannot find id: ' + id + ' in classTab: ' + this.sign, url: ''})
        return JSON.parse(getVar(this.sign + id, defaultClass))
    },
    setUrl(url) {
        return url.replace(/\$\{([^}]*)\}/g, (_, id) => this.getClass(id).url)
    }
}
// 生成头部分类
const fyAll = RULE.url.includes('fyAll')
let tabHeader = []
if (RULE.class_name) tabHeader.push({
    id: fyAll ? 'fyAll' : 'fyclass',
    class_name: RULE.class_name,
    class_url: RULE.class_url
})
if (RULE.area_name) tabHeader.push({
    id: fyAll ? 'fyAll' : 'fyarea',
    class_name: RULE.area_name,
    class_url: RULE.area_url
})
if (RULE.year_name) tabHeader.push({
    id: fyAll ? 'fyAll' : 'fyyear',
    class_name: RULE.year_name,
    class_url: RULE.year_url
})
if (RULE.sort_name) tabHeader.push({
    id: fyAll ? 'fyAll' : 'fysort',
    class_name: RULE.sort_name,
    class_url: RULE.sort_url
})
tabHeader = new ClassTab(tabHeader, { name: RULE.title, fold: readFile('tabFold'), })
