function 添加防止网页(项url, UA) {

    //上级跳转到当前页的链接必须是hiker://empty；不然一刷新还是会重复加载
    //UA ''或false或'手机' 默认是手机端 ; 电脑 'PC'

    var 刷新重复 = MY_RULE.title + "网页源码"
    var pc链接 = 项url.replace("m.", "www.")
    var 手机链接 = 项url.replace("www.", "m.")

    var code;
    if (getVar(刷新重复, "false") == "false") {

        code = UA == 'PC' ? fetch(pc链接) : request(手机链接);
        putVar(刷新重复, getParam('url') + "￥&￥" + code)

    } else {

        var pm = getVar(刷新重复).split("￥&￥")
        if (pm[0] == getParam('url')) {
            code = pm[1]
        } else {
            code = UA == 'PC' ? fetch(pc链接) : request(手机链接);
            putVar(刷新重复, getParam('url') + "￥&￥" + code)
        }
    }
    return code
}


