"},{"col_type":"movie_3","name":"链接处理","path":"urlParse","rule":"// 首页链接替换分类
function indexUrl(param) {
    function runCode(rule) {
        try {
            let [input, code] = rule.split('.js:')
            return code ? eval(code) : rule
        } catch (e) { return rule }
    }
    let { RULE, pageNum, QING_TITLE } = JSON.parse(param.split('###')[1])
    RULE = JSON.parse(base64Decode(RULE))
    pageNum = parseInt(pageNum)
    let url = RULE.url

    eval(JSON.parse(fetch('hiker://page/ClassTab?rule=' + QING_TITLE)).rule)
    url = tabHeader.setUrl(fyAll ? url.replace(/fyAll/g, '$${fyAll}') : url.replace(/fy(class|area|year|sort)/g, '$${fy$1}'))
    url = url.replace(/fypage(?:@(-?\d+)@)?(?:\*(\d+)@)?/g, (_, start, space) => parseInt(start || 0) + 1 + (pageNum - 1) * parseInt(space || 1))
    url = /^([\s\S]*?)(?:\[firstPage=([\s\S]*?)\])?(?:(\.js:[\s\S]*?))?$/.exec(url)
    if (pageNum == 1 && url[2]) url[1] = url[2]
    if (url[3] && !url[1].includes('.js:')) url[1] += url[3]
    return runCode(url[1])
}
// 链接参数处理
function paramHandle(urlParam, ua, urlTmp) {
    let charset, UserAgent
    try { // 链接的编码和ua应该是首先继承首页链接
        [, , charset, UserAgent] = urlTmp.split(';')
        UserAgent = UserAgent.match(/^{(.*)}$/)[1].split('&&').find((v) => v.startsWith('User-Agent@'))
    } catch (e) {}
    urlParam[0] = urlParam[0] || 'GET'
    urlParam[1] = urlParam[1] || charset || 'UTF-8'
    try {
        urlParam[2] = urlParam[2].match(/^{(.*)}$/)[1].split('&&')
    } catch (e) { urlParam[2] = [] }
    // 添加全局UA
    if (!urlParam[2].some((v) => v.startsWith('User-Agent@'))) {
        if (UserAgent) urlParam[2].push(UserAgent)
        else if (ua == 'pc')
            urlParam[2].push('User-Agent@' + PC_UA.replace(/;/g, '；；').replace(/\?/, '？？'))
        else if (ua == 'mobile')
            urlParam[2].push('User-Agent@' + MOBILE_UA.replace(/;/g, '；；').replace(/\?/, '？？'))
    }
    urlParam[2] = '{' + urlParam[2].join('&&') + '}'
    return urlParam.join(';')
}
"}]

