} else {
    let [_, findRule, detailFindRule] = MY_RULE.find_rule.match(/^([\s\S]*?)(?:==>([\s\S]*))?$/)
    findRule = findRule.split(';')
    parseDomForArray(getResCode(), findRule.shift()).forEach((data) => {
        let [title, img, desc, url] = findRule.map((v, i) => {
                try {
                    if (v == '*') return ''
                    else v = (i == 1 || i == 3) ?
                        parseDom(data, v) :
                        parseDomForHtml(data, v)
                    if (i != 3) v = runCode(v)
                    return v
                } catch (e) { return '' }
            }),
            res = {
                title: title, url: url,
                desc: desc, img: img,
                col_type: MY_RULE.col_type
            }
        if (res.url) {
            if (res.url.includes('@lazyRule=')) {
                let reIndex = res.url.indexOf('.js:')
                if (reIndex >= 0) res.url = res.url.slice(0, reIndex) + '.js:\nif(MY_RULE)Object.assign(MY_RULE, ' + assignLazyRule + ');' + res.url.slice(reIndex + 4)
            } else  if (detailFindRule) {
                res.extra = {
                    url: genExUrl(url),
                    RULE: Object.assign(genMyRule(), { detail_find_rule: detailFindRule }),
                    pageTitle: isIndex && title
                }
                res.url = 'hiker://page/frameLoad?rule=' + QING_TITLE
            } else if (MY_RULE.detail_find_rule) {
                res.extra = {
                    url: genExUrl(url),
                    RULE: genMyRule(),
                    pageTitle: isIndex && title
                }
                res.url = 'hiker://page/frameLoad?rule=' + QING_TITLE
            }
        }
        myCollection_el.push(res)
    })
    setResult(myCollection_el)
}
"},{"col_type":"movie_3","name":"数据载入","path":"dataLoad","rule":"$.exports = function(showAll) {
