"},{"col_type":"movie_3","name":"页面载入","path":"frameLoad","rule":"js:
const QING_TITLE = getMyVar('myCollection')
let myCollection_el = [],
    { RULE, isIndex, pageTitle } = MY_PARAMS,
    thisRULE = $.require('hiker://page/dataLoad?rule=' + QING_TITLE)().find((v) => v.title == RULE.title)
if (isIndex) {
    RULE = thisRULE
} else {
    RULE.pages = thisRULE.pages
}
eval(JSON.parse(fetch('hiker://page/preFunc?rule=' + QING_TITLE)).rule)
eval(JSON.parse(fetch('hiker://page/urlParse?rule=' + QING_TITLE)).rule)

if (isIndex) {
    if (MY_PAGE == 1) {
        // 加载ClassTab组件
        eval(JSON.parse(fetch('hiker://page/ClassTab?rule=' + QING_TITLE)).rule)
        tabHeader.load(myCollection_el)
        if (RULE.search_url) myCollection_el.push({
            title: "搜索",
            desc: "搜你想要的...",
            url: '"hiker://search?s="+input',
            extra: {
                rules: $.toString((QING_TITLE, title) => {
                    let rules = $.require('hiker://page/dataLoad?rule=' + QING_TITLE)()
                    rules = [rules.find((v) => v.title == title)]
                    return JSON.stringify(rules)
                }, QING_TITLE, RULE.title),
                defaultValue: getMyVar('searchKey', ''),
                onChange: "putMyVar('searchKey',input)"
            },
            col_type: "input",
        })
    }
    // 处理MY_RULE
    MY_RULE.url = RULE.url
    MY_RULE.col_type = RULE.col_type
    MY_RULE.detail_col_type = RULE.detail_col_type
    MY_RULE.find_rule = RULE.find_rule
    MY_RULE.detail_find_rule = RULE.detail_find_rule
    MY_RULE.params = {}
} else {
    MY_RULE.url = MY_PARAMS.url
    MY_RULE.col_type = RULE.detail_col_type
    MY_RULE.find_rule = RULE.detail_find_rule
    MY_RULE.params = RULE.params
}
MY_RULE.pages = RULE.pages
MY_RULE.pageList = JSON.parse(MY_RULE.pages || '[]')
MY_RULE.title = RULE.title

if (!isIndex) {
    if (pageTitle) setPageTitle(pageTitle)
    setLastChapterRule(thisRULE.last_chapter_rule)
    MY_PARAMS = MY_RULE.params
}
// 预处理并初始化config
if (MY_PAGE == 1) {
    let loaded = getMyVar(MY_RULE.title + "@pre")
    if (!loaded || isIndex) {
        eval(thisRULE.preRule)
        putMyVar(MY_RULE.title + "@pre", "1")
    }
}
let _cfg = getMyVar('initConfig', '{}')
if (_cfg && _cfg.length > 0) {
    config = JSON.parse(_cfg)
}

// 正文解析通用变量
let assignLazyRule = $.toString((QING_TITLE, title, url) => {
    let RULE = $.require("hiker://page/dataLoad?rule=" + QING_TITLE)().find((v) => v.title == title)
    return { title:title, url: url, pages: RULE.pages, pageList: JSON.parse(RULE.pages || '[]') }
}, QING_TITLE, MY_RULE.title, MY_RULE.url)
function genMyRule() {
    let rule = Object.assign({}, MY_RULE)
    delete rule.last_chapter_rule
    delete rule.preRule
    delete rule.pageList
    delete rule.pages
    return rule
}
function runCode(rule) {
    try {
        let [input, code] = rule.split('.js:')
        return code ? eval(code) : rule
    } catch (e) { return rule }
}
function genExUrl(url) {
    url = url.split(';')
    return url.shift() + ';' + paramHandle(url, thisRULE.ua, MY_RULE.url)
}
// 正文解析
if (MY_RULE.find_rule.startsWith('js:')) {
    setResult = function(el, param1, param2, param3) {
        param1 = CALLBACK_KEY
        param2 = MY_RULE
        param3 = MY_TYPE
        if (Array.isArray(el.data)) el = el.data
        Array.prototype.push.apply(myCollection_el, el.map((v) => {
            if(!v.extra) v.extra = {}
            v.col_type = v.col_type || MY_RULE.col_type
            let MY__RULE = Object.assign(genMyRule(), { params: v.extra })

            if (typeof v.url != 'string' || ['rule', 'pics', 'toast', 'input', 'confirm', 'copy', 'editFile', 'x5', 'x5WebView', 'x5Play', 'web', 'select', 'x5Rule', 'webRule', 'download', 'share'].find((vv) => v.url.startsWith(vv + '://')) || ['x5_webview_single', 'input'].find((vv) => v.col_type == vv)) {
                return v
            } else if (v.url.includes('@rule=')) {
                let [_, url, rule] = v.url.match(/^([\s\S]*?)@rule=([\s\S]*)$/)
                v.url = url
                MY__RULE.detail_find_rule = rule
                MY__RULE.detail_col_type = MY_RULE.col_type
            } else if (v.url.startsWith('hiker://page/')) {
                if (v.url.includes('rule=') || v.extra.rule)
                    return v
                let [_, path, flag, params] = v.url.match(/^hiker:\/\/page\/(.+?)(#.*?)?(?:\?(.*))?$/),
                    subPage = MY_RULE.pageList.find((v) => v.path == path),
                    subUrl = (params || '').split('&').find((v) => v.startsWith('url='))
                v.url = (subUrl ? subUrl.slice(4).replace(/？？/g, '?').replace(/＆＆/g, '&') : (v.extra || {}).url) || 'hiker://empty' + flag + '?' + (params || '')
                MY__RULE.detail_find_rule = subPage.rule
                MY__RULE.detail_col_type = subPage.col_type
            } else if (v.url.includes('@lazyRule=')) {
                let reIndex = v.url.indexOf('.js:')
                if (reIndex >= 0) v.url = v.url.slice(0, reIndex) + '.js:\nif(MY_RULE)Object.assign(MY_RULE, ' + assignLazyRule + ');' + v.url.slice(reIndex + 4)
                return v
            } else if (isIndex) {
                if (!MY_RULE.detail_find_rule || /hiker:\/\/(?!empty.+$)/.test(v.url)) return v
            } else {
                return v
            }
            v.extra = Object.assign({}, v.extra, {
                url: genExUrl(v.url),
                RULE: MY__RULE,
                pageTitle: isIndex && v.title
            })
            v.url = 'hiker://page/frameLoad?rule=' + QING_TITLE
            return v
        }))
        method_setResult.invoke(javaContext, myCollection_el, param1, param2, param3)
    }
    setHomeResult = setResult
    setSearchResult = setResult

    eval(MY_RULE.find_rule.slice(3))
} else {
    let [_, findRule, detailFindRule] = MY_RULE.find_rule.match(/^([\s\S]*?)(?:==>([\s\S]*))?$/)
    findRule = findRule.split(';')
    parseDomForArray(getResCode(), findRule.shift()).forEach((data) => {
