const rendererFilter = function(d, data, key, cb){
  const primaryColor = '#f47983';
  if(!data || !data.length || data.length == 1) {
	return false;
  }
  data.forEach(item => {
	var title = item.val == getVar(key) ? "““””<b>"+'<span style="color: '+ primaryColor +'">⭐ '+item.name+'</span></b>' : item.name;
	d.push({
	  title: title,
	  url: $("#noLoading#").lazyRule((key, val, cb)=>{
		putVar(key,val);
