var res = {};
var items = [];
var html = getResCode();

var list = parseDomForArray(html, '.myui-vodlist__media&&li');

for (var i in list) {
    var title = parseDomForHtml(list[i], 'a&&title');
    var img = parseDom(list[i], '.lazyload&&data-original') + '@Referer=';
    var url = parseDom(list[i], 'a&&href') + "#immersiveTheme#";
    var desc = parseDomForHtml(list[i], '.pic-text&&Text');
    var con = parseDomForHtml(list[i], '.detail--h4&&Text');
    items.push({
        title: title,
        img: img,
        url: url,
        desc: desc,
        content: con
    });
}

res.data = items;
setSearchResult(res);

