var d = [];
var html = getResCode();
var title = parseDomForHtml(html, 'h1&&Text');

var jsUrl = fetch('hiker://files/cache/MyParseSet.json');
if (jsUrl == '' || !fetch(JSON.parse(jsUrl).cj)) {
    var jsFile = fetch('https://gitee.com/Duan-Nian/Dn/raw/master/hikerview/CloudParse-V2_Dn.js');
} else {
    var jsFile = fetch(JSON.parse(jsUrl).cj);
}
eval(jsFile);
//免嗅
var lazy = `@lazyRule=.embed-responsive&&script&&Html.js:eval(input.replace(/player_.*?={/,'player_aaaa={'));var jsurl=player_aaaa.url;if(jsurl.match(/youku|mgtv|ixigua|qq.com|qiyi|migu|bilibili|sohu|pptv|letv|le|alizy/)){var input=jsurl;` + lazy + `}else{jsurl}`;

var arts = parseDomForArray(html, '.nav&&a');
var tabs = [];
for (var i in arts) {
    tabs.push(parseDomForHtml(arts[i], 'a&&Text'))
}

var conts = parseDomForArray(html, 'body&&.myui-content__list');
var lists = [];
for (var i in conts) {
    lists.push(parseDomForArray(conts[i], 'ul&&li'))
}

d.push({
    title: parseDomForHtml(html, '.data&&Text').replace('分类|', '').replace('地区：', '').replace('年份：', ' ').substring(0, 20) + '\n' + parseDomForHtml(html, '.data,2&&Text').substring(0, 20) + '\n' + parseDomForHtml(html, '.data,1&&Text').substring(0, 20),
    desc: ' 简 介：' + parseDomForHtml(html, '.data,4&&Text').substring(0, 45),
    pic_url: parseDom(html, '.lazyload&&data-original') + '@Referer=',
    url: MY_URL,
    col_type: 'movie_1_vertical_pic_blur'
});

d.push({
    col_type: 'line_blank'
});
d.push({
    col_type: 'big_blank_block'
});
d.push({
    col_type: 'big_blank_block'
});
function setTabs(tabs, vari) {
    for (var i in tabs) {
        var url = "#noLoading#@lazyRule=.js:putVar('" + vari + "', '" + i + "');refreshPage(false);'toast://切换成功！';'#noHistory#hiker://empty'";
        d.push({
            title: getVar(vari, '0') == i ? '““' + tabs[i] + '””' : tabs[i],
            url: url,
            col_type: 'scroll_button'
        })
    }
}

function setLists(lists, index) {
    d.push({
        title: '🔍搜索',
        url: 'hiker://search?s=' + title,
        col_type: 'scroll_button'
    })
    d.push({
        title: '🔥设置',
        url: setUrl,
        col_type: 'scroll_button'
    })
    d.push({
        title: '豆瓣',
        url: 'hiker://search?s=' + title + '&rule=道娃•R',
        col_type: 'scroll_button'
    })
    d.push({
        title: '搜狗',
        url: 'hiker://search?s=' + title + '&rule=搜狗·Q',
        col_type: 'scroll_button'
    })
    d.push({
        title: '香情',
        url: 'hiker://search?s=' + title + '&rule=香情影视',
        col_type: 'scroll_button'
    })
    d.push({
        title: 'APP',
        url: 'hiker://search?s=' + title + '&rule=APP影视',
        col_type: 'scroll_button'
    })
    d.push({
        title: '采集网',
        url: 'hiker://search?s=' + title + '&rule=资源网采集.xyq',
        col_type: 'scroll_button'
    })
    d.push({
        col_type: 'line_blank'
    });
    d.push({
        col_type: 'big_blank_block'
    });
    d.push({
        col_type: 'big_blank_block'
    });
    var list = lists[index];
    if (getVar('shsort') == '1') {
        for (var j = list.length - 1; j >= 0; j--) {
            var jm = parseDomForHtml(list[j], 'a&&Text').replace(/第(\d+)集/, '$1').replace('第', '').replace('期', '');
            d.push({
                title: jm,
                url: parseDom(list[j], 'a&&href') + lazy,
                col_type: jm.length > 5 ? 'scroll_button' : 'scroll_button'
            });
        }
    } else {
        for (var j = 0; j < list.length; j++) {
            var jm = parseDomForHtml(list[j], 'a&&Text').replace(/第(\d+)集/, '$1').replace('第', '').replace('期', '');
            d.push({
                title: jm,
                url: parseDom(list[j], 'a&&href') + lazy,
                col_type: jm.length > 5 ? 'scroll_button' : 'scroll_button'
            });
        }
    }
}
setTabs(tabs, MY_URL);
setLists(lists, getVar(MY_URL, '0'));
d.push({
    title: '<br>',
    col_type: 'big_blank_block'
});
d.push({
    col_type: 'line_blank'
});
d.push({
    title: (getVar('shsort') == '1') ? '““””<b><span style="color: #FF0000">『逆序⚠️猜你喜欢 』</span></b>' : '““””<b><span style="color: #1aad19">『正序⚠️猜你喜欢 』</span></b>',
    url: `@lazyRule=.js:let conf = getVar('shsort');if(conf=='1'){putVar({key:'shsort', value:'0'});}else{putVar({key:'shsort', value:'1'})};refreshPage();'toast://切换排序成功'`,
    col_type: 'text_center_1'
})
var listxc = parseDomForArray(getResCode(), '.myui-vodlist__bd&&li');

for (var i in listxc) {
    try {
        d.push({
            title: parseDomForHtml(listxc[i], 'a&&title'),
            desc: parseDomForHtml(listxc[i], '.pic-text&&Text'),
            pic_url: parseDom(listxc[i], '.myui-vodlist__box&&.myui-vodlist__thumb&&style') + '@Referer=',
            url: $(parseDom(listxc[i], 'a&&href') + "#immersiveTheme#").rule(() => {
                eval(JSON.parse(fetch('hiker://page/erji', {})).rule)
            }),
            col_type: 'movie_3'
        });
    } catch (e) {}
}
setResult(d);

