detail_find_rule:
js:
var d = [];
var html = getResCode();

var arts = parseDomForArray(html,'body&&.nav-tabs&&li');
var tabs = [];
for (var i in arts) {
    tabs.push(parseDomForHtml(arts[i], 'Text'))
}

var conts = parseDomForArray(html, 'body&&.stui-content__playlist');
var lists =[];
for (var i in conts) {
    lists.push(parseDomForArray(conts[i], 'ul&&li'))
}

d.push({
	title: parseDomForHtml(html, '.data&&Text').replace('类型|','').replace('地区：','').replace('年份：',' ').substring(0, 18)+ '\n' + parseDomForHtml(html,'.data,1&&Text').substring(0, 15)+'\n'+parseDomForHtml(html, '.data,2&&Text').substring(0, 15),
	desc:parseDomForHtml(html,'.data,3||#desc&&Text').substring(0, 16),
	pic_url: parseDom(html, '.lazyload&&data-original'),
	url: MY_URL,
	col_type: 'movie_1_vertical_pic'
});
var des_desc = parseDomForHtml(html, 'body&&.stui-content__detail--h1--&&Text');
 d.push({
          title: des_desc.length > 50 ? des_desc.replace(des_desc, des_desc.substring(0, 50) +'...‘‘’’<font color="#0aa344">详情</font></small>') : des_desc,
          url: 'hiker://empty#' + parseDomForHtml(html,'body&&.stui-content__detail&&Html').split('/h1>')[1].replace(/;/g,'').replace(/<li/g,'<br><ul') + `@rule=js:var res = {}; var d = [];d.push({title: MY_URL.split('hiker://empty#')[1],col_type: 'rich_text'});res.data = d; setHomeResult(res);`,     
          col_type: 'flex_button'
});


var lazy =  `@lazyRule=body&&.stui-player__video&&script&&Html.js:eval(input.replace(/player_.*?={/,'player_aaaa={'));var url=player_aaaa.url;url.indexOf('m3u8')!=-1?url:url.split('/share')[0]+fetch(url,{}).match(/url: '(.*?)'/)[1]`;

function setTabs(tabs, vari) {
        d.push({
            title: '‘‘线路’’',
            url: `@lazyRule=.js:let conf = getVar('折叠');if(conf=='关'){putVar({key:'折叠', value:'开'});}else{putVar({key:'折叠', value:'关'})};refreshPage(false);'toast://切换成功'`,
            col_type: 'flex_button'
        })
if (getVar('折叠') == '开'||getVar('折叠') == '') {
        var title = '';
        for (var i = 0; i < tabs.length; i++) {
            var url = "@lazyRule=.js:putVar('" + vari + "', '" + i + "');refreshPage(false);'toast://切换成功！'";
            d.push({
                title: tabs[i] + (getVar(vari, '0') == i ? '✅' : ''),
                url: url,
                col_type: 'flex_button'
            })
        }
        d.push({
            col_type: 'line_blank'
        })
    }
}
    function setLists(lists, index) {
        d.push({
            title: '‘‘选集’’',
            url: `@lazyRule=.js:let conf = getVar('shsort');if(conf==' - 逆序'){putVar({key:'shsort', value:' - 正序'});}else{putVar({key:'shsort', value:' - 逆序'})};refreshPage(false);'toast://切换排序成功'`,
            col_type: 'flex_button'
        })
        var list = lists[index];

if (getVar('shsort') == ' - 逆序') {
            for (var j = list.length - 1; j >= 0; j--) {
                d.push({
                title: parseDomForHtml(list[j], 'Text'),
                url: parseDom(list[j], 'a&&href') + lazy,
                col_type: 'flex_button'
                });
            }
        } else {
            for (var j = 0; j < list.length; j++) {
                d.push({
                title: parseDomForHtml(list[j], 'a&&Text'),
                url: parseDom(list[j], 'a&&href') + lazy,
                col_type: 'flex_button'
                });
            }
        }
    }
setTabs(tabs, MY_URL);
setLists(lists, getVar(MY_URL, '0'));

d.push({title: '<br>', col_type: 'rich_text'});

setResult(d);
