js:
var d = [];
var html=getResCode();
//默认播放地址(需修改)
var lazy='@lazyRule=.stui-player__video&&script&&Html.js:eval(input);player_aaaa.url';

try{
var des_title = parseDomForHtml(html, 'body&&.stui-pannel&&h1&&Text');//ok
}catch(e){
var des_title ='没有正确获取到影片名称哟！';
}
try{
var des_pic =parseDom(html,'.lazyload&&data-original');//ok
}catch(e){
var des_desc ='没有正确获取到简介图片路径哟！\n'+des_desc;
}
try{
var des_desc = parseDomForHtml(html,'body&&.stui-pannel&&p,2&&Text');
}catch(e){
var des_desc ='没有正确获取到剧情简介哟！';
}
var arts = parseDomForArray(html,'body&&.stui-pannel__head');//线路列表
var conts = parseDomForArray(html, 'body&&.stui-content__playlist');//播放列表

var tabs = [];
for (var i in conts) {
    tabs.push(parseDomForHtml(arts[i], 'h3&&Text'))   //线路名
}

var lists =[];
for (var i in conts) {
    lists.push(parseDomForArray(conts[i], 'ul&&li'))    //' 列表名改'ul&&li'
}


//下面除了获取选集名称例如1,2,3集，有的需要改下！
//不需要就不要修改

d.push({
title:des_title,
desc:des_desc,
pic_url:des_pic,
url:getUrl(),
col_type:'movie_1_vertical_pic'
});
//折叠需要用到的

function setTabs(tabs, vari) {
     d.push({
        title: '‘‘🔎线路’’',
        col_type: 'text_center_1'
    })
    var title = '';
   if(tabs.length != '')  {
    for (var i = 0; i < tabs.length; i++) {
        var url = "hiker://empty@lazyRule=.js:putVar('"+vari+"', '"+i+"');refreshPage();'toast://切换成功！'";

        d.push({
           title: (getVar(vari, '0')==i?'🚩':'')+tabs[i] ,
           url: url,
           col_type: tabs.length>2?'text_3':'text_2'
        })
    }

 }else{d.push({title:'当前线路暂无线路,请检查线路是否正确',url:'toast://当前线路暂无线路,请检查线路是否正确',col_type: 'text_1'});}     

    d.push({        col_type: 'line_blank'    })
}
if (getVar('shsort') == '1') {var sx='‘‘’’<font color="#0aa344">(逆序)</font></small>';}else {var sx='‘‘’’<font color="#0aa344">  (正序)</font></small>';}
    function setLists(lists, index) {
        d.push({
           // title: '‘‘选集’’',
            title: '‘‘选集’’' +'〰️'+ sx,
            url: `#noLoading#@lazyRule=.js:let conf = getVar('shsort');if(conf=='1'){putVar({key:'shsort', value:'0'});}else{putVar({key:'shsort', value:'1'})};refreshPage();'toast://切换排序成功';'#noHistory#hiker://empty'`,
            col_type: 'text_center_1'
        })

if(lists.length != '')  {
        var list = lists[index];
if (getVar('shsort') == '1') {
            for (var j = list.length - 1; j >= 0; j--) {
                d.push({
                title: parseDomForHtml(list[j], 'Text'),
                url: parseDom(list[j], 'a&&href') + lazy,
                col_type: list.length > 3 ? 'text_3' : 'text_2'
                });
            }
        } else {
            for (var j = 0; j < list.length; j++) {
                d.push({
                title: parseDomForHtml(list[j], 'Text'),
                url: parseDom(list[j], 'a&&href') + lazy,
                col_type: list.length > 3 ? 'text_3' : 'text_2'
                });
            }
        }

        }else{d.push({title:'当前线路暂无集数,请检查选集是否正确',url:'toast://当前线路暂无集数,请检查选集是否正确',col_type: 'text_1'});}    
      d.push({title:'<br>',col_type:'rich_text' });      
    }

// 调用函数
setTabs(tabs, MY_URL);
setLists(lists, getVar(MY_URL, '0'));

setResult(d);

