js:
var d = [];
var html = getResCode();

var arts = parseDomForArray(html, '.nav-tabs&&li');
var tabs = [];
for (var i in arts) {
    tabs.push(parseDomForHtml(arts[i], 'a&&Text'))
}

var conts = parseDomForArray(html, '.tab-content&&ul');
var lists = [];
for (var i in conts) {
    lists.push(parseDomForArray(conts[i], 'ul&&li'))
}

d.push({
    title: parseDomForHtml(html, '.data&&Text').replace('地区：', '   ').replace('年份：', '   ') + '\n' + parseDomForHtml(html, '.data,1&&Text').substring(0, 15) + '\n' + parseDomForHtml(html, '.data,2&&Text').substring(0, 15),
    desc: parseDomForHtml(html, '.data,3&&Text').substring(0, 25),
    pic_url: parseDom(html, '.lazyload&&data-original'),
    url: MY_URL,
    col_type: 'movie_1_vertical_pic'
});

var lazy=`@lazyRule=.js:JSON.parse(request(input).match(/r player_.*?=(.*?)</)[1]).url`

function setTabs(tabs, vari) {
    for (var i = 0; i < tabs.length; i++) {

        d.push({
            title: getVar(vari, '0') == i ? '““' + tabs[i] + '””' : tabs[i],
            url: "#noLoading#@lazyRule=.js:putVar('" + vari + "', '" + i + "');refreshPage(false);'toast://切换成功！';'#noHistory#hiker://empty'",
            col_type: 'flex_button'
        })
    }
    d.push({ col_type: 'blank_block' });
}
function setLists(lists, index) {
    d.push({
        title: '‘‘正逆序：’’',
        url: `#noLoading#@lazyRule=.js:let conf = getVar('shsort');if(conf==' - 逆序'){putVar({key:'shsort', value:' - 正序'});}else{putVar({key:'shsort', value:' - 逆序'})};refreshPage(false);'toast://切换排序成功';'#noHistory#hiker://empty'`,
        col_type: 'flex_button'
    })
    var list = lists[index];

    if (getVar('shsort') == ' - 逆序') {
        for (var j = list.length - 1; j >= 0; j--) {
            d.push({
                title: parseDomForHtml(list[j], 'Text'),
                url: parseDom(list[j], 'a&&href')+ lazy,
                col_type: 'flex_button'
            });
        }
    } else {
        for (var j = 0; j < list.length; j++) {
            d.push({
                title: parseDomForHtml(list[j], 'a&&Text'),
                url: parseDom(list[j], 'a&&href')+lazy,
                col_type: 'flex_button'
            });
        }
    }
}
setTabs(tabs, MY_URL);
setLists(lists, getVar(MY_URL, '0'));

d.push({ title: '<br>', col_type: 'rich_text' });
setResult(d);

