        for (var j = list.length - 1; j >= 0; j--) {
            var jm = parseDomForHtml(list[j], 'a&&Text').replace(/第(\d+)集/, '$1').replace('第', '').replace('期', '');
            d.push({
                title: jm,
                url: parseDom(list[j], 'a&&href') + lazy,
