js:
var html = getResCode();

var jsUrl = fetch('hiker://files/cache/MyParseSet.json');
    if(jsUrl == '' || !fetch(JSON.parse(jsUrl).cj)){var jsFile = fetch('https://gitee.com/Duan-Nian/Dn/raw/master/hikerview/CloudParse-V2_Dn.js');}else{var jsFile = fetch(JSON.parse(jsUrl).cj);}eval(jsFile);
//免嗅
var lazy = `@lazyRule=.js:var jsurl = JSON.parse(request(input).match(/r player_.*?=(.*?)</)[1]).url;if(jsurl.match(/youku|mgtv|ixigua|qq.com|qiyi|migu|bili|sohu|pptv|letv|le/)){var input=jsurl;` + lazy + `}else{jsurl}`;


//线路
var playList = [];
var arts = parseDomForArray(html, '.play_source_tab&&a');

for (var i in arts) {
    playList.push({
        name: parseDomForHtml(arts[i], 'Text').replace(' ',''),
        list: []
    });
}
//列表
playList[0].use = true;
var conts = parseDomForArray(html, 'body&&.content_playlist');

/*以下为x5头部设置*/
try {
//导演
    var daoyan = parseDomForHtml(html, '.returl&&p,5&&Text').substring(3, 26);
//演员
    var zhuyan = parseDomForHtml(html, '.returl&&p,6&&Text').substring(3, 45);
var time=parseDomForHtml(html,'.play-detail&&.remarks&&Text').replace('状态：','');
} catch (e) {
    var daoyan = zhuyan = "未知";
}
var data = {
    myhistory: {
        url: MY_URL,
        ruleName: MY_RULE.title
    }, //固定
    name: parseDomForHtml(html, 'h1&&Text'),
    pic: parseDom(html, '.returl&&img&&src'),
    info: [ 
          {type: '导演：',inf: daoyan},
          {type: '演员：',inf: zhuyan},
          {type: '状态：',inf: time}
    ],
//片类
   class: parseDomForHtml(html, '.returl&&p,4&&Text').replace('类型：','').substring(3, 06),
//年代
    year: parseDomForHtml(html, '.returl&&p,1&&Text').replace('年代：',''),
//地区
    area: parseDomForHtml(html, '.returl&&p&&Text').replace('地区：',''),
//剧情简介
    desc: parseDomForHtml(html, '.desc_txt&&Text'),
lazy:true,
  plugin: $.toString(() => {
        var config = fetch('hiker://files/cache/MyParseSet.json');
        if (config == '' || !fetch(JSON.parse(config).cj)) {
            var jsUrl = 'https://code.aliyun.com/AI957/Hiker/raw/master/v/CloudParse-V2_Dn.js';
        } else {
            var jsUrl = JSON.parse(config).cj;
        }
        eval(fetch(jsUrl));
        var d = [];
        setParse();
        setResult(d);
    })
}
/*以上为x5头部设置*/

for (let i = 0; i < conts.length; i++) {
    playList[i].list = parseDomForArray(conts[i], "ul&&li").map(item => {
        return {
            title: parseDomForHtml(item, "a&&Text"),
            url: parseDom(item, 'a&&href') + lazy,
        }
    });
}
//setError(playList)
data.playList = playList;
putVar('movieDetail', data);
setResult([{
    desc: 'auto&&list',
    url: 'file:///storage/emulated/0/Android/data/com.example.hikerview/files/Documents/rules/x5page/style_pro.html',
    col_type: 'x5_webview_single'
}]);

