let 推荐链 = 'a&&href'



let html = getResCode();
let Color = "#f13b66a";
let Color1 = "#098AC1";
let Color2 = "#19B89D";
let Colorbs = function() {
    return '#' + ('00000' + (Math.random() * 0x1000000 << 0).toString(16)).substr(-6);     
};

try {
    let conts = pdfa(html, 列表);
    var lists = [];
    for (let i in conts) {
        lists.push(pdfa(conts[i], 子列表))
    }
} catch (e) {};

try {
    var 名 = pdfh(html, 片名).replace(/电影|电视剧|纪录片|动漫|B站/g, '')
    var 类 = '类型：' + pdfh(html, 类型).replace(/\/|分类：|电影|连续剧|动漫|类型：/g, '')
    var 导 = '导演：' + pdfh(html, 导演).replace(/\/|导演：/g, ' ')
    var 主 = '主演：' + pdfh(html, 主演).replace(/\/|主演：|演员：/g, ' ')
    var 更 = '更新：' + pdfh(html, 更新).replace(/\/|更新：|更新:|状态：|状态:/g, ' ')
    var 状 = '状态：' + pdfh(html, 状态).replace(/\/|状态：|状态:|更新：|更新:/g, ' ')
    var 图 = pd(html, 图片)
    var 介 = pdfh(html, 简介).replace(/　|展开|收起|详情：|[收起部分]|概要 :|©豆瓣/g, '')
} catch (e) {};

let titles = 导.substring(0, 16) + '\n' + 主.substring(0, 16) + '\n' + 更.substring(0, 16);
let descs = '片名：' + 名 + '\n' + 类 + '\n' + '\n' + 导 + '\n' + 主 + '\n' + '\n' + 更 + '\n' + 状 + '\n' + '\n' + '剧情简介：' + '\n' + 介;

d.push({
    title: '‘‘’’<b><font color="#b0e0e6">' + titles + '</font></b>',
    desc: '‘‘’’<b><b><font color="#708090">简介：</b>' + '<font color="#778899">' + 介.substr(0, 40) + '...</font><small><font color="#996666">查看详情</font></font></small></b>',
    pic_url: 图,
    url: $().rule((text) => {
        setResult([{
            title: text,
            col_type: 'long_text',
        }])
    }, descs),
    col_type: 'movie_1_vertical_pic',
    extra: {
        gradient: true
    }
});

