                var url = "download://" + urll
            } else {
                var url = urll.replace(/.*\]|.*】|.*盘\：|.*接：|提取码.*/g, '');
            }
            d.push({
                title: jm,
                url: url,
                col_type: 'text_center_1',
                extra: {
                    id: url,
                    blockRules: ['.css', '.gif', '.jpg', '.jpeg', '.png', '.ico', '.svg', 'cnzz', 'google', 'xn--*:*', 'hm.baidu.com', '/ads/*.js', '.m3u8', '.mp4'],
                    jsLoadingInject: true
                }
            });
        }
    } catch (e) {
        d.push({
            title: '暂无片源',
            url: MY_URL,
            col_type: 'text_center_1'
        });
    }
};

